}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-feature-settings: "rlig" 1, "calt" 1;
  }

  h1, h2, h3, h4, h5, h6 {
    @apply font-semibold tracking-tight;
  }

  h1 {
    @apply text-4xl lg:text-5xl;
  }

  h2 {
    @apply text-3xl lg:text-4xl;
  }

  h3 {
    @apply text-2xl lg:text-3xl;
  }

  h4 {
    @apply text-xl lg:text-2xl;
  }

  /* Custom scrollbar */
  ::-webkit-scrollbar {
    width: 6px;
  }

  ::-webkit-scrollbar-track {
    @apply bg-gray-100;
  }

  ::-webkit-scrollbar-thumb {
    @apply bg-gray-300 rounded-full;
  }

  ::-webkit-scrollbar-thumb:hover {
    @apply bg-gray-400;
  }
}

@layer components {
  /* Custom button variants matching design */
  .btn-primary {
    @apply bg-primary hover:bg-primary/90 text-primary-foreground;
  }

  .btn-secondary {
    @apply bg-secondary hover:bg-secondary/90 text-secondary-foreground;
  }

  .btn-accent {
    @apply bg-accent hover:bg-accent/90 text-accent-foreground;
  }

  /* Hero section styling */
  .hero-gradient {
    background: linear-gradient(135deg, var(--primary) 0%, hsl(210, 25%, 10%) 100%);
  }

  /* Product card hover effects */
  .product-card {
    @apply transition-all duration-300 hover:shadow-xl hover:-translate-y-1;
  }

  /* Glass morphism effect */
  .glass {
    backdrop-filter: blur(10px);
    background: hsl(0, 0%, 100%, 0.8);
    border: 1px solid hsl(0, 0%, 100%, 0.2);
  }

  .glass-dark {
    backdrop-filter: blur(10px);
    background: hsl(0, 0%, 0%, 0.8);
    border: 1px solid hsl(0, 0%, 100%, 0.1);
  }
}

@layer utilities {
  /* Custom utilities */
  .text-balance {
    text-wrap: balance;
  }

  .animate-fade-in {
    animation: fadeIn 0.5s ease-in-out;
  }

  .animate-slide-up {
    animation: slideUp 0.6s ease-out;
  }

  .animate-scale-in {
    animation: scaleIn 0.3s ease-out;
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }

  @keyframes slideUp {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes scaleIn {
    from {
      opacity: 0;
      transform: scale(0.9);
    }
    to {
      opacity: 1;
      transform: scale(1);
    }
  }

  /* Focus styles */
  .focus-ring {
    @apply focus:ring-2 focus:ring-ring focus:ring-offset-2 focus:outline-none;
  }

  /* Custom spacing */
  .section-padding {
    @apply py-16 lg:py-24;
  }

  .container-padding {
    @apply px-4 sm:px-6 lg:px-8;
  }
}

