// Allow theme customization by overriding specific properties
export const createTheme = (overrides: Partial<ThemeConfig>): ThemeConfig => {
  return {
    ...defaultTheme,
    ...overrides,
    colors: {
      ...defaultTheme.colors,
      ...overrides.colors,
    },
    fonts: {
      ...defaultTheme.fonts,
      ...overrides.fonts,
    },
    layout: {
      ...defaultTheme.layout,
      ...overrides.layout,
    },
    contact: {
      ...defaultTheme.contact,
      ...overrides.contact,
    },
    social: {
      ...defaultTheme.social,
      ...overrides.social,
    },
  };
};

